import { useForm } from "react-hook-form";
import { useEffect, useState } from "react";
import api from "../../services/api";
import { useAuth } from "../../Providers/Auth";
import Button from "../../components/Button";
import { CardHabit } from "../../components/Card/styles";
import HeaderDashboard from "../../components/HeaderDashboard";
import { PContainer, CardContainer, CardNewHabit } from "./styles";
import Popup from "../../components/Modal";
import * as yup from "yup";
import { yupResolver } from "@hookform/resolvers/yup";
import CircularProgress from "@material-ui/core/CircularProgress";
import TextField from "@material-ui/core/TextField";
import Autocomplete from "@material-ui/lab/Autocomplete";

const Dashboard = () => {
  const [token] = useState(
    JSON.parse(localStorage.getItem("Habits:token")) || ""
  );
  const { userId } = useAuth();

  const [habits, setHabits] = useState([]);
  const [finishedHabits, setFinishedHabits] = useState([]);
  const [openNewHabit, setOpenNewHabit] = useState(false);
  const [loading, setLoading] = useState(true);

  const schema = yup.object().shape({
    habit: yup.string().required("Campo obrigatório"),
    category: yup.string().required("Campo obrigatório"),
    difficulty: yup.string().required("Campo obrigatório"),
    frequency: yup.string().required("Campo obrigatório"),
  });

  const {
    register,
    handleSubmit,
    reset,
    formState: { errors },
  } = useForm({
    resolver: yupResolver(schema),
  });

  const loadHabits = () => {
    api
      .get("/habits/personal/", {
        headers: {
          Authorization: `Bearer ${token}`,
        },
      })
      .then((response) => {
        setHabits(response.data.filter((habit) => habit.achieved === false));
        setFinishedHabits(
          response.data.filter((habit) => habit.achieved === true)
        );
        setLoading(false);
      })
      .catch((err) => console.log(err));
  };

  useEffect(() => {
    loadHabits();
  });

  const addNewHabit = ({ habit, category, difficulty, frequency }) => {
    api
      .post(
        "/habits/",
        {
          title: habit,
          category: category,
          difficulty: difficulty,
          frequency: frequency,
          achieved: false,
          how_much_achieved: 0,
          user: userId,
        },
        {
          headers: {
            Authorization: `Bearer ${token}`,
          },
        }
      )
      .then((response) => {
        loadHabits();
        reset();
      });

    setOpenNewHabit(false);
  };

  const handleDelete = ({ id }) => {
    api.delete(`/habits/${id}/`, {
      headers: {
        Authorization: `Bearer ${token}`,
      },
    });
  };

  const handleUpdate = ({ id, frequency, how_much_achieved }) => {
    let achieved = how_much_achieved;

    if (achieved + 9 >= 100 || achieved + 5 >= 100 || achieved + 13 >= 100) {
      api.patch(
        `/habits/${id}/`,
        {
          achieved: true,
          how_much_achieved: 100,
        },
        {
          headers: {
            Authorization: `Bearer ${token}`,
          },
        }
      );
    } else if (frequency === "5") {
      api.patch(
        `/habits/${id}/`,
        {
          achieved: false,
          how_much_achieved: (achieved += 100 / 20),
        },
        {
          headers: {
            Authorization: `Bearer ${token}`,
          },
        }
      );
    } else if (frequency === "3") {
      api.patch(
        `/habits/${id}/`,
        {
          achieved: false,
          how_much_achieved: Math.ceil((achieved += 100 / 12)),
        },
        {
          headers: {
            Authorization: `Bearer ${token}`,
          },
        }
      );
    } else {
      api.patch(
        `/habits/${id}/`,
        {
          achieved: false,
          how_much_achieved: Math.ceil((achieved += 100 / 8)),
        },
        {
          headers: {
            Authorization: `Bearer ${token}`,
          },
        }
      );
    }
  };

  return (
    <>
      <HeaderDashboard />
      <PContainer>
        Em progresso
        <Button
          onClick={() => {
            setOpenNewHabit(true);
          }}
        >
          + Novo Hábito
        </Button>
      </PContainer>
      {openNewHabit && (
        <Popup>
          <CardNewHabit>
            <form onSubmit={handleSubmit(addNewHabit)}>
              <section>
                <TextField
                  id="standard-basic"
                  label="Novo Hábito"
                  {...register("habit")}
                  name="habit"
                  required
                />

                <div className="error"> {errors.habit?.message}</div>
                <TextField
                  id="standard-basic"
                  label="Categoria"
                  {...register("category")}
                  name="category"
                />
                <div className="error"> {errors.category?.message}</div>
                <TextField
                  id="standard-basic"
                  label="Dificuldade"
                  {...register("difficulty")}
                  name="difficulty"
                />
                <div className="error"> {errors.difficulty?.message}</div>
                <TextField
                  placeholder="Frequência"
                  {...register("frequency")}
                  name="frequency"
                />
                <div className="error"> {errors.frequency?.message}</div>

                <Button type="submit">Adicionar</Button>
              </section>
            </form>
          </CardNewHabit>
        </Popup>
      )}
      <CardContainer>
        <h2>Em progresso</h2>
        {loading ? (
          <CircularProgress size={50} />
        ) : (
          habits.map((habit) => (
            <CardHabit key={habit.id}>
              <div class="habit-container">
                <div class="habit-title">{habit.title}</div>
                <hr />
                <div class="habit-difficulty">
                  <p>Fácil</p>
                </div>
                <div class="habit-progression">
                  <h3>{habit.how_much_achieved}</h3>
                </div>
                <div class="progress-bar"></div>
                <div class="habit-category">
                  <p>Categoria</p>
                </div>
                <div class="container-button">
                  <button
                    class="habit-button-giveup"
                    onClick={() => handleDelete(habit)}
                  >
                    Desistir
                  </button>
                  <button
                    class="habit-button"
                    onClick={() => handleUpdate(habit)}
                  >
                    Progredir
                  </button>
                </div>
              </div>
            </CardHabit>
          ))
        )}
      </CardContainer>
      <CardContainer>
        <h2>Concluidos</h2>
        {loading ? (
          <CircularProgress size={50} />
        ) : (
          finishedHabits.map((habit) => (
            <CardHabit key={habit.id}>
              <div class="habit-container">
                <div class="habit-title">{habit.title}</div>
                <hr />
                <div class="habit-difficulty">
                  <p>Fácil</p>
                </div>
                <div class="habit-progression">
                  <h3>{habit.how_much_achieved}%</h3>
                </div>
                <div class="progress-bar"></div>
                <div class="habit-category">
                  <p>Categoria</p>
                </div>
              </div>
            </CardHabit>
          ))
        )}
      </CardContainer>
    </>
  );
};

export default Dashboard;
