import { useForm } from "react-hook-form";
import Button from "../../components/Button";
import { CardHabit } from "../../components/Card/styles";
import HeaderDashboard from "../../components/HeaderDashboard";
import {
  PContainer,
  CardContainer,
  CardNewHabit,
  FormContainer,
  PageContainer,
} from "./styles";
import Popup from "../../components/Modal";
import * as yup from "yup";
import { yupResolver } from "@hookform/resolvers/yup";
import CircularProgress from "@material-ui/core/CircularProgress";
import TextField from "@material-ui/core/TextField";
import ArrowBackIcon from "@material-ui/icons/ArrowBack";
import { useHabits } from "../../Providers/Habits";
import DeleteForeverIcon from "@material-ui/icons/DeleteForever";
import DoubleArrowIcon from "@material-ui/icons/DoubleArrow";
import MenuItem from "@material-ui/core/MenuItem";
import { makeStyles } from "@material-ui/core/styles";
import React from "react";

import ProgressBar from "../../components/ProgressBar";

const Dashboard = () => {
  const schema = yup.object().shape({
    title: yup.string().required("Campo obrigat贸rio"),
    category: yup.string().required("Campo obrigat贸rio"),
    difficulty: yup.string().required("Campo obrigat贸rio"),
    frequency: yup.string().required("Campo obrigat贸rio"),
  });

  const currencies = [
    {
      value: "1",
      label: "1 vez por semana",
    },
    {
      value: "2",
      label: "2 vezes por semana",
    },
    {
      value: "3",
      label: "3 vezes por semana",
    },
    {
      value: "4",
      label: "4 vezes por semana",
    },
    {
      value: "5",
      label: "5 vezes por semana",
    },
  ];

  const useStyles = makeStyles((theme) => ({
    root: {
      "& .MuiTextField-root": {
        margin: theme.spacing(1),
        width: "25ch",
        fontsize: "60px",
      },
    },
  }));

  const classes = useStyles();

  const {
    habits,
    addNewHabit,
    handleDelete,
    finishedHabits,
    handleUpdate,
    loading,
    openNewHabit,
    setOpenNewHabit,
  } = useHabits();

  const {
    register,
    handleSubmit,
    reset,
    formState: { errors },
  } = useForm({
    resolver: yupResolver(schema),
  });

  const [currency, setCurrency] = React.useState("2");

  const handleChange = (event) => {
    setCurrency(event.target.value);
  };

  const onSubmitFunction = (data) => {
    addNewHabit(data);
    reset();
  };

  return (
    <>
      <HeaderDashboard />
      <PContainer>
        Em progresso
        <Button
          onClick={() => {
            setOpenNewHabit(true);
          }}
        >
          + Novo H谩bito
        </Button>
      </PContainer>
      {openNewHabit && (
        <Popup>
          <CardNewHabit>
            <FormContainer>
              <form
                onSubmit={handleSubmit(onSubmitFunction)}
                className={classes.root}
                autoComplete="off"
              >
                <section>
                  <h1> Cadastre seu mais novo h谩bito ! </h1>

                  <TextField
                    id="standard-basic"
                    label="Novo H谩bito"
                    {...register("habit")}
                    name="habit"
                  />
                  <div className="error"> {errors.habit?.message}</div>
                  <TextField
                    id="standard-basic"
                    label="Categoria"
                    {...register("category")}
                    name="category"
                  />
                  <div className="error"> {errors.category?.message}</div>
                  <TextField
                    id="standard-basic"
                    label="Dificuldade"
                    {...register("difficulty")}
                    name="difficulty"
                  />
                  <div className="error"> {errors.difficulty?.message}</div>
                  <TextField
                    id="standard-select-currency"
                    select
                    label="Frequ锚ncia"
                    helperText="Selecione a frequ锚ncia"
                    {...register("frequency")}
                    name="frequency"
                  >
                    {currencies.map((option) => (
                      <MenuItem
                        key={option.value}
                        value={option.value}
                        onChange={handleChange}
                      >
                        {option.label}
                      </MenuItem>
                    ))}
                  </TextField>

                  <Button type="submit">Adicionar</Button>

                  <p>
                    <ArrowBackIcon
                      cursor="pointer"
                      onClick={() => document.location.reload()}
                    />
                  </p>
                </section>
              </form>
            </FormContainer>
          </CardNewHabit>
        </Popup>
      )}
      <CardContainer>
        {loading ? (
          <CircularProgress size={50} />
        ) : (
          habits.map((habit) => (
            <CardHabit key={habit.id}>
              <div className="habit-container">
                <div className="habit-title">H谩bito: {habit.title}</div>
                <hr />
                <div className="habit-difficulty">
                  <p>Dificuldade: {habit.difficulty}</p>
                </div>
                <hr />
                <div className="habit-category">
                  <p>Categoria: {habit.category}</p>
                </div>
                <hr />
                <div className="habit-progression">
                  <p>Progresso:</p>
                </div>
                <ProgressBar
                  color="yellow"
                  completed={`${habit.how_much_achieved}%`}
                />
                <div className="container-button">
                  <button
                    className="habit-button-giveup"
                    onClick={() => handleDelete(habit)}
                  >
                    <DeleteForeverIcon />
                  </button>
                  <button
                    className="habit-button"
                    onClick={() => handleUpdate(habit)}
                  >
                    <DoubleArrowIcon />
                  </button>
                </div>
              </div>
            </CardHabit>
          ))
        )}
      </CardContainer>
      <PContainer>Conclu铆dos</PContainer>
      <CardContainer>
        {loading ? (
          <CircularProgress size={50} />
        ) : (
          finishedHabits.map((habit) => (
            <CardHabit key={habit.id}>
              <div className="habit-container">
                <div className="habit-title">H谩bito: {habit.title}</div>
                <hr />
                <div className="habit-difficulty">
                  <p>Dificuldade: {habit.difficulty}</p>
                </div>
                <hr />
                <div className="habit-category">
                  <p>Categoria: {habit.category}</p>
                </div>
                <hr />
                <div className="habit-progression">
                  <p>Progresso:</p>
                </div>
                <ProgressBar
                  color="blue"
                  completed={`${habit.how_much_achieved}%`}
                />
              </div>
            </CardHabit>
          ))
        )}
      </CardContainer>
    </>
  );
};

export default Dashboard;
